tinymce.PluginManager.add("addimageright", function(a, b) {
    var state;

    /* Actions to do on button click */
    function Klik() {
        state = !state; /* Switching state */
        a.fire('addimageright', {state: state});
        htmldata = "";
        
        htmldata+= '&nbsp;';
        htmldata += '<div class="row">';
            htmldata += '<div class="col-sm-6 col-xs-12">';
                htmldata += "<p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>";
            htmldata += '</div>';
            htmldata += '<div class="col-sm-6 col-xs-12">';
                htmldata += '<div class="imagebody-right">';
                    htmldata += '<div class="image-right">';
                        htmldata += '<img class="img-responsive" src='+b+'/img/image1.jpg>';
                        htmldata += '<div class="caption" style="font-size:12px;">';
                            htmldata += '<br/><font class="photographer">Foto : Petugas melakukan pengecekan fisik kendaraan di Polres Sleman, DI Yogyakarta, Rabu (4/1). </font> Donang Wahyu , Katadata.  <br/>';
                        htmldata += '</div>';
                    htmldata += '</div>';
                htmldata += '</div>';
            htmldata += '</div>';
        htmldata += '</div>';
        htmldata+= '&nbsp;';
        a.insertContent(htmldata);
    }

    /* Adding the button & command */
    a.addCommand('cmd_addimageright', Klik);

    a.addButton("addimageright", {
        text: "Gambar Kanan",
        // icon: 1,
        // image: b + '/img/summary.png',
        cmd: 'cmd_addimageright',
    }), a.addMenuItem("addimageright", {
        text: "Gambar Kanan",
        // icon: 1,
        // image: b + '/img/summary.png',
        context: "format",
        cmd: 'cmd_addimageright',
    })
});