tinymce.PluginManager.add("embed_chart", function(a, b) {
    a.addButton("embed_chart", {
        text: "Embed Chart",
        icon: 1,
        image: b + '/img/icon.png',
        onclick: function() {
            a.windowManager.open({
                title: "Embed Chart",
             
                body: [{
                type: "textbox",
                name: "sources",
                multiline: !0,
                minWidth: a.getParam("code_dialog_width", 600),
                minHeight: a.getParam("code_dialog_height", Math.min(tinymce.DOM.getViewPort().h - 200, 500)),
                spellcheck: !1,
                style: "direction: ltr; text-align: left"
                }],
                onsubmit: function(b) {
                	htmldata = b.data.sources;
                    a.insertContent(htmldata)
                }
            })
        }
    }), a.addMenuItem("embed_chart", {
        text: "Embed Chart",
        icon: 1,
        image: b + '/img/icon.png',
        context: "insert",
         onclick: function() {
            a.windowManager.open({
                title: "Embed Chart",
             
                body: [{
                type: "textbox",
                name: "sources",
                multiline: !0,
                minWidth: a.getParam("code_dialog_width", 600),
                minHeight: a.getParam("code_dialog_height", Math.min(tinymce.DOM.getViewPort().h - 200, 500)),
                spellcheck: !1,
                style: "direction: ltr; text-align: left"
                }],
                onsubmit: function(b) {
                    htmldata = b.data.sources;
                    a.insertContent(htmldata)
                }
            })
        }
    })
});