tinymce.PluginManager.add("embed_grafik", function(a, b) {
    a.addButton("embed_grafik", {
        text: "Embed Grafik",
        icon: 1,
        image: b + '/img/icon.png',
        onclick: function() {
            a.windowManager.open({
                title: "Embed Grafik",
                width: 600,
                height: 100,
                body: [{
                    type: "textbox",
                    name: "source",
                    label: "Image Source"
                }],
                onsubmit: function(b) {
                	htmldata = b.data.source;
                    a.insertContent(htmldata)
                }
            })
        }
    }), a.addMenuItem("embed_grafik", {
        text: "Embed Grafik",
        icon: 1,
        image: b + '/img/icon.png',
        context: "insert",
        onclick: function() {
            a.windowManager.open({
                title: "Embed Grafik",
                url: b + "/dialog.html",
                width: 600,
                height: 400,
                buttons: [{
                    text: "Insert",
                    onclick: function() {
                        var b = a.windowManager.getWindows()[0];
                        a.insertContent(b.getContentWindow().document.getElementById("content").value), b.close()
                    }
                }, {
                    text: "Close",
                    onclick: "close"
                }]
            })
        }
    })
});