tinymce.PluginManager.add('gambaranalisis', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('gambaranalisis', {
        text: 'Insert Gambar Analiss',
        icon: true,
        image: url + '/img/icon.png',
        onclick: function() {
            editor.windowManager.open({
                title: 'Insert Gambar',
                url: base_web_url + 'images/listtinymceanalis',
                width: 800,
                height: 400,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('gambaranalisis', {
        text: 'Insert Gambar Analisis',
        context: 'insert',
        icon: true,
        image: url + '/img/icon.png',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Insert Gambar',
                url: base_web_url + 'images/listtinymceanalis',
                width: 800,
                height: 400,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});