tinymce.PluginManager.add('insertlinks', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('insertlinks', {
        text: 'Insert Links Article',
        icon: true,
        image: url + '/img/icons.svg',
        onclick: function() {
            editor.windowManager.open({
                title: 'Insert Links Article',
                url: base_web_url + 'content/listtinymce',
                width: 800,
                height: 400,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('insertlinks', {
        text: 'Insert Links Article',
        context: 'insert',
        icon: true,
        image: url + '/img/icons.svg',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Insert Links Article',
                url: base_web_url + 'content/listtinymce',
                width: 800,
                height: 400,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});